import { MergeDeep } from 'type-fest'

import {
  EnrollmentStatus,
  EventUserRole,
  EventUserRole,
  EventStatus,
  EventType,
  SlideStatus,
} from './enums'
import { Database as DatabaseGenerated } from './supabase-db'

export { Json } from './supabase-db'

type DatabaseTypeOverrides = {
  public: {
    Tables: {
      enrollment: {
        Row: {
          event_role: EventUserRole | null
          status: EnrollmentStatus | null
        }
      }
      event: {
        Row: {
          type: EventType | null
          status: EventStatus | null
        }
      }
      slide: {
        Row: {
          status: SlideStatus | null
        }
      }
    }
  }
}

// Override the type for a specific column in a view:
export type Database = MergeDeep<DatabaseGenerated, DatabaseTypeOverrides>

export type SupabaseTables<
  PublicTableNameOrOptions extends
    | keyof (Database['public']['Tables'] & Database['public']['Views'])
    | { schema: keyof Database },
  TableName extends PublicTableNameOrOptions extends { schema: keyof Database }
    ? keyof (Database[PublicTableNameOrOptions['schema']]['Tables'] &
        Database[PublicTableNameOrOptions['schema']]['Views'])
    : never = never,
> = PublicTableNameOrOptions extends { schema: keyof Database }
  ? (Database[PublicTableNameOrOptions['schema']]['Tables'] &
      Database[PublicTableNameOrOptions['schema']]['Views'])[TableName] extends {
      Row: infer R
    }
    ? R
    : never
  : PublicTableNameOrOptions extends keyof (Database['public']['Tables'] &
        Database['public']['Views'])
    ? (Database['public']['Tables'] &
        Database['public']['Views'])[PublicTableNameOrOptions] extends {
        Row: infer R
      }
      ? R
      : never
    : never

export type SupabaseTablesInsert<
  PublicTableNameOrOptions extends
    | keyof Database['public']['Tables']
    | { schema: keyof Database },
  TableName extends PublicTableNameOrOptions extends { schema: keyof Database }
    ? keyof Database[PublicTableNameOrOptions['schema']]['Tables']
    : never = never,
> = PublicTableNameOrOptions extends { schema: keyof Database }
  ? Database[PublicTableNameOrOptions['schema']]['Tables'][TableName] extends {
      Insert: infer I
    }
    ? I
    : never
  : PublicTableNameOrOptions extends keyof Database['public']['Tables']
    ? Database['public']['Tables'][PublicTableNameOrOptions] extends {
        Insert: infer I
      }
      ? I
      : never
    : never

